CREATE TABLE IF NOT EXISTS Domain(id INTEGER PRIMARY KEY AUTOINCREMENT, name TEXT UNIQUE, description TEXT);
CREATE TABLE IF NOT EXISTS Kingdom(id INTEGER PRIMARY KEY AUTOINCREMENT, domain_id INTEGER, name TEXT UNIQUE, description TEXT, FOREIGN KEY(domain_id) REFERENCES Domain(id));
ALTER TABLE Phylum ADD COLUMN kingdom_id INTEGER REFERENCES Kingdom(id);
CREATE TABLE IF NOT EXISTS Class(id INTEGER PRIMARY KEY AUTOINCREMENT, phylum_id INTEGER, name TEXT UNIQUE, description TEXT, FOREIGN KEY(phylum_id) REFERENCES Phylum(id));
CREATE TABLE IF NOT EXISTS Ord(id INTEGER PRIMARY KEY AUTOINCREMENT, class_id INTEGER, name TEXT UNIQUE, description TEXT, FOREIGN KEY(class_id) REFERENCES Class(id));
CREATE TABLE IF NOT EXISTS Family(id INTEGER PRIMARY KEY AUTOINCREMENT, order_id INTEGER, name TEXT UNIQUE, description TEXT, FOREIGN KEY(order_id) REFERENCES Ord(id));
ALTER TABLE Genus ADD COLUMN family_id INTEGER REFERENCES Family(id);